mapper.select:
SELECT a.BOARD_TYPE ,a.BOARD_NUM ,a.BOARD_TITLE ,a.BOARD_COMMENT ,COM_CODE.CODE_ID ,COM_CODE.CODE_NAME FROM ( SELECT BOARD_TYPE ,BOARD_NUM ,BOARD_TITLE ,BOARD_COMMENT ,ROW_NUMBER() OVER(ORDER BY BOARD_NUM ASC) AS NUMROW ,COUNT(*) OVER() AS TOTAL_CNT FROM BOARD
WHERE BOARD_TYPE IN
#{type}
) a INNER JOIN COM_CODE ON a.BOARD_TYPE = COM_CODE.CODE_ID WHERE a.NUMROW BETWEEN (#{pageNo}-1)*10 + 1 AND (#{pageNo})*10

SELECT COUNT(*) AS TOTAL_CNT FROM BOARD
WHERE BOARD_TYPE IN
#{type}

mapper.update:
UPDATE BOARD 
SET
BOARD_TITLE = #{boardTitle}
,BOARD_COMMENT = #{boardComment}
,MODIFIER = 'SYSTEM'
,MODIFIED_TIME = TO_CHAR(SYSDATE)
WHERE
BOARD_TYPE = #{boardType} AND BOARD_NUM = #{boardNum}
