mapper.insert:

    		SELECT TO_CHAR(RECRUIT_SEQ.NEXTVAL) FROM DUAL

        INSERT INTO
        RECRUIT
        	(
			SEQ
			,NAME
        	,BIRTH
        	,PHONE
        	,EMAIL
        	,ADDR
        	,LOCATION
        	,WORK_TYPE
        	,SUBMIT
        	) 
		VALUES 
			(
			#{seq}
			,#{name}
			,#{birth}
			,#{phone}
			,#{email}
			,#{addr}
			,#{location}
			,#{workType}
			,#{submit}
			)

mapper.update:
        UPDATE RECRUIT
        SET
			SEQ = #{seq}
			,NAME = #{name}
        	,BIRTH = #{birth}
        	,PHONE = #{phone}
        	,EMAIL = #{email}
        	,ADDR = #{addr}
        	,LOCATION = #{location}
        	,WORK_TYPE = #{workType}
        	,SUBMIT = #{submit}
		WHERE
			SEQ = #{seq}
