mapper.insert:

    		SELECT TO_CHAR(CAR_SEQ.NEXTVAL) FROM DUAL

        INSERT INTO
        CAREER
        	(
			CAR_SEQ
			,SEQ
        	,START_PERIOD
        	,END_PERIOD
        	,COMP_NAME
        	,TASK
        	,LOCATION
        	) 
		VALUES 
			(
			#{carSeq}
			,#{seq}
			,#{startPeriod}
			,#{endPeriod}
			,#{compName}
			,#{task}
			,#{location}
			)

mapper.update:
        UPDATE CAREER
        SET
			CAR_SEQ = #{carSeq}
			,SEQ = #{seq}
        	,START_PERIOD = #{startPeriod}
        	,END_PERIOD = #{endPeriod}
        	,COMP_NAME = #{compName}
        	,TASK = #{task}
        	,LOCATION = #{location}
		WHERE 
			CAR_SEQ = #{carSeq}
